import { configureStore } from '@reduxjs/toolkit';
import cakeReducer from '../features/cake/cakeSlice';
import icedCreamReducer from '../features/icedcream/icedCreamSlice';
import userReducer from '../features/user/userSlice';
import productReducer from '../features/product/productSlice';
import authorReducer from '../features/author/authorSlice';
import itemlistReducer from '../features/item/itemlistSlice';
import { itemlistApi } from '../features/item/ItemlistService';
// import { createLogger } from 'redux-logger';


// const logger = createLogger()
const store = configureStore({
    reducer: {
        cake: cakeReducer,
        icedCream: icedCreamReducer,
        user: userReducer,
        product: productReducer,
        author: authorReducer,
        itemlist: itemlistReducer,
        [itemlistApi.reducerPath]: itemlistApi.reducer
    },
    middleware: (getDefaultMiddleware) => {
        return getDefaultMiddleware().concat(itemlistApi.middleware);
    }
});

setupListeners(store.dispatch)

export default store;
