import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProduct } from './StockService';

const StockView = () => {
    const product = useSelector((state) => state.stockitem.stock);
    const dispatch = useDispatch();
    const { id } = useParams();

    useEffect(() => {
        dispatch(fetchProduct("http://localhost:5000/api/stock/" + id));
    }, [id, dispatch]);

    return (
        <div key={product.id}>
            <p>Name : {product.name}</p>
            <p>Price : {product.price}</p>
        </div>
    );
};

export default StockView;
