import { type } from '@testing-library/user-event/dist/type';
import React, { useState } from 'react';
type AuthUser = {
    firstName: string
    email: string
}
// Mention the type of the initial value as well as future value
const User = () => {
    const [user, setUser] = useState<AuthUser | null>(null)
                // OR
    let [userInfo, setUserInfo] = useState<AuthUser>({firstName: '', email: ''} as AuthUser)

    const handleLogin = () => {
        setUser({
            firstName: 'Presley',
            email: 'presleyomozuwa90@gmail.com'
        })
    }
    const handleLogout = () => {
        setUser(null)
    }
    return (
        <div>
            <button onClick={handleLogin}>Login</button>
            <button onClick={handleLogout}>Logout</button>
            <p>First Name : {user?.firstName}</p>
            <p>Email : {user?.email}</p>
        </div>
    );
};

export default User;
