import React, { useReducer } from 'react';
type StateDataType = {
    count: number
}
type ActionDataType = {
    type: string
    payload: number
}

const initialState = {
    count: 0
}

const reducer = (state: StateDataType, action: ActionDataType) => {
    switch (action.type) {
        case 'INCREMENT':
            return {
                count: state.count + action.payload
            }
        case 'DECREMENT':
            return {
                count: state.count - action.payload
            }
        default: return state
    }
}

const Counter = () => {
const [state, dispatch] = useReducer(reducer, initialState)

    return (
        <div>
            <div>
                <h3>Count: {state.count}</h3>
                <button onClick={() => dispatch({
                    type: 'INCREMENT',
                    payload: 10
                })}>Increment</button>
            </div>
            <div>
                <h3>Count: {state.count}</h3>

            </div>
        </div>
    );
};

export default Counter;
